#!/usr/bin/env python# -*- coding:utf-8 -*-# @Time : 2024/11/20# @Author : cyq# @File : interfaceApi# @Software: PyCharm# @Desc:from fastapi import APIRouter, Depends, BackgroundTasks, Response as FastResponse, Form, UploadFile, Filefrom app.controller import Authenticationfrom app.mapper.interface import InterfaceScriptMapper, InterfaceMapperfrom app.model.base import Userfrom app.response import Responsefrom app.schema.interface import *from app.schema.interface.interfaceApiSchema import TryScriptSchemafrom interface.exec import ExecSafeScriptfrom interface.starter import APIStarterfrom utils import MyLoguru, logfrom interface.runner import InterFaceRunnerfrom utils.curlTrans import CurlConverterfrom common.locust_client import locust_clientfrom utils.fileManager import FileManagerfrom utils import GenerateToolsLOG = MyLoguru().get_logger()router = APIRouter(prefix="/interface", tags=['自动化接口步骤'])API_Exclude_Field = {    "params", "headers", "extracts", "after_script", "asserts", "before_db_id", "before_params", "url"                                                                                                 "before_script",    "before_sql", "before_sql_extracts", "body", "body_type", "connect_timeout",    "follow_redirects"}@router.post("/setInterfaceModule", description="设置接口部分")async def set_interface_Module(info: SetInterfacesModuleSchema, _=Depends(Authentication())):    await InterfaceMapper.set_interfaces_modules(**info.model_dump())    return Response.success()@router.post("/insert", description="添加步骤")async def insert_interface_Api(ApiInfo: AddInterfaceApiSchema, auth=Depends(Authentication())):    api = await InterfaceMapper.save(        creatorUser=auth,        **ApiInfo.model_dump())    return Response.success(api)@router.get("/detail", description="接口信息")async def detail_interface(interfaceId: int, _=Depends(Authentication())):    inter = await InterfaceMapper.get_by_id(interfaceId)    log.info(f"detail interface  {inter.body}")    return Response.success(inter)@router.post("/copy", description="复制")async def detail_interface(interfaceId: CopyInterfaceApiSchema, copyer=Depends(Authentication())):    inter = await InterfaceMapper.copy_api(        is_common=True,        apiId=interfaceId.id,        creator=copyer    )    return Response.success(inter)@router.post("/tryScript", description="调试脚本")async def try_script(scriptInfo: TryScriptSchema, _=Depends(Authentication())):    sc = ExecSafeScript()    try:        data = sc.execute(scriptInfo.script)        return Response.success(data)    except Exception as e:        # 保留老代码的异常处理        return Response.success(str(e))@router.get("/queryBy", description="批量查询")async def query_by_interface(inter: InterfaceApiFieldSchema, _=Depends(Authentication())):    inters = await InterfaceMapper.get_by(**inter.model_dump(        exclude_unset=True,        exclude_none=True,    ))    return Response.success(inters)@router.post("/page", description="分页查询")async def page_interface(inter: PageInterfaceApiSchema, _=Depends(Authentication())):    inters = await InterfaceMapper.page_by_module(        **inter.model_dump(            exclude_unset=True,            exclude_none=True        )    )    return Response.success(data=inters, exclude=API_Exclude_Field)@router.post("/pageNoModule", description="分页查询")async def page_interface_no_module(inter: PageInterfaceApiSchema, _=Depends(Authentication())):    inters = await InterfaceMapper.page_query(        module_id=None,        **inter.model_dump(            exclude_unset=True,            exclude_none=True        )    )    return Response.success(inters, exclude=API_Exclude_Field)@router.post("/update", description="修改接口")async def update_interface(inter: UpdateInterfaceApiSchema, auth=Depends(Authentication())):    log.info(f"update interface  {inter}")    api = await InterfaceMapper.update_by_id(**inter.model_dump(        exclude_unset=True,    ), updateUser=auth)    ## 当更新了data    await InterfaceMapper.update_data_file(api)    return Response.success()@router.post("/remove", description="删除")async def remove_interface(inter: RemoveInterfaceApiSchema, _=Depends(Authentication())):    try:        # 保留老代码的引用检查异常处理        await InterfaceMapper.remove(inter.id)        return Response.success()    except Exception as e:        # 捕获组关联异常并返回友好提示        if "接口被" in str(e) and "个组引用" in str(e):            return Response.error(str(e))        log.error(f"删除接口失败: {str(e)}")        return Response.error("删除接口失败，请查看日志")@router.post("/try", description="调试")async def try_interface_Api(interId: TryAddInterfaceApiSchema, user=Depends(Authentication())):    """api 执行 """    resp = await InterFaceRunner(        starter=APIStarter(user),    ).try_interface(interface=interId.interfaceId)    LOG.info(f"{resp}")    return Response.success([resp])@router.get("/query/script_doc", description="Api可用脚本")async def get_interface_script_doc(_: User = Depends(Authentication())):    docs = await InterfaceScriptMapper.query_all()    return Response.success(docs)@router.post("/transCurl", description="转换curl")async def tansCurlScript(script: CurlSchema, _: User = Depends(Authentication())):    """转换curl"""    try:        info = CurlConverter(script.script).parse_curl()        return Response.success(info)    except Exception:        return Response.error("解析失败、请检查")@router.post("/debugPerf", description="调试执行")async def debug_performance(background_tasks: BackgroundTasks,                            interface_id: int = Form(..., description="接口ID"),  # 使用更明确的参数名                            perf_user: int = Form(..., description="并发用户数"),                            perf_duration: float = Form(..., description="测试持续时间(秒)"),                            perf_spawn_rate: int = Form(..., description="用户生成速率"),                            wait_range: str = Form(..., description="等待时间范围"),                            use_var: bool = Form(..., description="替换变量"),                            api_file: UploadFile | None = File(None, description="API文件"),  # 添加描述                            user: User = Depends(Authentication())):    # 1. 创建性能测试配置    perf_setting = PerfSchema(        interfaceId=interface_id,        perf_user=perf_user,        perf_duration=perf_duration,        perf_spawn_rate=perf_spawn_rate,        wait_range=wait_range    )    log.info(f"性能测试配置: {perf_setting}")    # 2. 获取接口信息    starter = APIStarter(user)    interface_runner = InterFaceRunner(starter=starter)    interface_info = await interface_runner.get_interface(interfaceId=interface_id,                                                          use_var=use_var)    log.debug(f"获取到的接口信息: {interface_info}")    # 3. 转换接口数据结构    interface_info['body'] = interface_info.pop('json', None)    interface_info['data'] = interface_info.pop('content', None)    api = InterfaceApiSchema(**interface_info)    task_id = f"interface_{GenerateTools.uid()}"    # 4. 处理上传文件    if api_file:        file_name = await FileManager.save_perf_file(            file=api_file,            interfaceId=task_id        )        perf_setting.file_name = file_name        log.info(f"文件已保存: {file_name}")    try:        # 5. 启动性能测试任务        background_tasks.add_task(            locust_client.start_locust,            api=api,            setting=perf_setting,            perf_api_name=task_id,            io=starter        )        log.info(f"性能测试任务已启动, task_id: {task_id}")        return Response.success(task_id)    except Exception as e:        log.exception(f"启动性能测试失败: {str(e)}")        return Response.error(f"启动性能测试失败: {str(e)}")@router.get("/stopPerf", description="停止性能测试")async def stop_perf(taskId: str, background_tasks: BackgroundTasks, user: User = Depends(Authentication())):    io = APIStarter(user)    try:        background_tasks.add_task(            locust_client.stop,            taskId=taskId,            io=io        )        return Response.success()    except Exception as e:        log.exception(f"停止性能测试失败: {str(e)}")        return Response.error(f"停止性能测试失败: {str(e)}")@router.post("/copy2Module", description="复制到")async def copy_to_module(copyInfo: Copy2Module, user: User = Depends(Authentication())):    """复制接口到模块"""    await InterfaceMapper.copy_to_module(user=user, **copyInfo.model_dump())    return Response.success()@router.get("/apisInfo/yaml", description="下载接口YAML")async def api_yaml_info(moduleId: int, _: User = Depends(Authentication())):    """下载接口YAML文件"""    import yaml    try:        interfaces = await InterfaceMapper.query_by(module_id=moduleId)        data = {"moduleId": moduleId, "apis": []}        if interfaces:            data["apis"] = [inter.map for inter in interfaces]        yaml_data = yaml.dump(data)        return FastResponse(            content=yaml_data,            media_type="text/yaml",            headers={"Content-Disposition": f"attachment; filename=interfaces_{moduleId}.yaml"}        )    except Exception as e:        log.exception(f"生成YAML失败: {str(e)}")        return Response.error(f"生成YAML失败: {str(e)}")