#!/usr/bin/env python# -*- coding:utf-8 -*-# @Time : 2025/8/4# @Author : cyq# @File : __init__.py# @Software: PyCharm# @Desc:from sqlalchemy import Column, String, INTEGER, ForeignKey, JSONfrom app.model.basic import BaseModelclass CaseHUb(BaseModel):    __tablename__ = "case_hub"    case_title = Column(String(250), nullable=False, comment="用例标题")    case_desc = Column(String(500), nullable=False, comment="用例描述")    case_level = Column(String(50), nullable=False, comment="用例等级")    case_mark = Column(String(250), nullable=True, comment="用例备注")    case_setup = Column(String(250), nullable=True, comment="用例前置")    case_type = Column(INTEGER, nullable=True, comment="用例类型 1普通 0 冒烟 。。")    case_info = Column(JSON, nullable=True, comment="用例信息")    case_creator = Column(INTEGER, nullable=True, comment="用例创建者")    case_creatorName = Column(String(250), nullable=True, comment="用例创建者名称")    module_id = Column(INTEGER, nullable=True, comment="所属模块")    project_id = Column(INTEGER, ForeignKey("project.id"), nullable=True,                        comment="所属产品")    def __repr__(self):        return f"Case <{self.case_title}>"