#!/usr/bin/env python# -*- coding:utf-8 -*-# @Time : 2025/8/25# @Author : cyq# @File : association# @Software: PyCharm# @Desc:from sqlalchemy import Column, Integer, ForeignKeyfrom app.model.basic import baseclass RequirementCaseAssociation(base):    __tablename__ = "requirement_case_association"    requirement_id = Column(Integer, ForeignKey('requirement.id', ondelete="CASCADE"), primary_key=True)    case_id = Column(Integer, ForeignKey('case.id', ondelete="CASCADE"), primary_key=True)