#!/usr/bin/env python# -*- coding:utf-8 -*-# @Time : 2025/5/30# @Author : cyq# @File : InterfaceCaseStepContent# @Software: PyCharm# @Desc:from sqlalchemy import Column, String, INTEGER, TEXTfrom app.model.basic import baseclass StepType:    WAIT = 1    SCRIPT = 2    DB = 3class InterfaceCaseStepContent(base):    """    业务用例 非接口步骤 表    #not todo    """    __tablename__ = "interface_case_step_content"    title = Column(String(255), nullable=False, comment="步骤名称")    step_type = Column(INTEGER, default=1, nullable=False, comment="步骤类型")    # 如果是wait    wait_time = Column(INTEGER, default=0, nullable=True, comment="等待时间")    #  如果是py script    script_value = Column(TEXT, nullable=True, comment="脚本内容")    #  如果是DB script    db_script = Column(TEXT, nullable=True, comment="数据库脚本")