#!/usr/bin/env python# -*- coding:utf-8 -*-# @Time : 2024/6/6# @Author : cyq# @File : _trigger# @Software: PyCharm# @Desc:from datetime import datetimefrom apscheduler.triggers.cron import CronTriggerclass Trigger(CronTrigger):    def __init__(self, cron: str):        values = cron.split()        super().__init__(minute=values[0],                         hour=values[1],                         day=values[2],                         month=values[3],                         day_of_week=values[4], )    @property    def next_runtime(self) -> "datetime":        now = datetime.now()        _t = self.get_next_fire_time(now, now)        return _t.strftime("%Y-%m-%d %H:%M:%S")