#!/usr/bin/env python# -*- coding:utf-8 -*-# @Time : 2025/7/3# @Author : cyq# @File : playConfigSchema# @Software: PyCharm# @Desc:from pydantic import BaseModel, Fieldfrom app.schema import PageSchemaclass AddPlayMethodSchema(BaseModel):    label: str    value: str    description: str | None = Field(None)    need_locator: int    need_value: intclass GetPlayMethodSchema(BaseModel):    uid: strclass EditPlayMethodSchema(BaseModel):    uid: str    label: str | None = Field(None)    value: str | None = Field(None)    description: str | None = Field(None)    need_locator: int | None = Field(None)    need_value: int | None = Field(None)class PagePlayMethodSchema(PageSchema):    label: str | None = Field(None)    value: str | None = Field(None)    need_locator: int | None = Field(None)    need_value: int | None = Field(None)