#!/usr/bin/env python# -*- coding:utf-8 -*-# @Time : 2025/7/8# @Author : cyq# @File : playStepGroupSchema# @Software: PyCharm# @Desc:from typing import Listfrom pydantic import BaseModel, Fieldfrom app.schema import PageSchemafrom enums import ModuleEnumclass InsertPlayStepGroupSchema(BaseModel):    name: str    description: str    is_group: bool = Field(True)    step_num: int = Field(0)    module_id: int    project_id: intclass RemovePlayStepGroupSchema(BaseModel):    groupId: intclass UpdatePlayStepGroupSchema(BaseModel):    id: int    name: str | None = Field(None)    description: str | None = Field(None)class GetPlaySubStepByIdSchema(BaseModel):    stepId: intclass PagePlayStepGroupSchema(PageSchema):    uid: str | None = Field(None, title="步骤组id")    name: str | None = Field(None, title="步骤组名称")    is_group: bool = Field(True)    module_id: int = Field(None, title="模块id")    project_id: int = Field(None, title="项目id")    module_type: int = Field(ModuleEnum.UI_STEP, title="模块类型")class ReOrderPlaySubStepsSchema(BaseModel):    groupId: int    stepIdList: List[int]class InsertPlaySubStepSchema(BaseModel):    name: str    description: str    method: str    locator: str | None = Field(None)    fill_value: str | None = Field(None)    iframe_name: str | None = Field(None)    new_page: bool = Field(False)    is_ignore: bool = Field(False)    is_common_step: bool | None = Field(False)    group_id: int    is_group: bool = Field(False)    module_id: int    project_id: int