#!/usr/bin/env python# -*- coding:utf-8 -*-# @Time : 2024/11/22# @Author : cyq# @File : execFunc# @Software: PyCharm# @Desc:from typing import List, Mapping, Any, Dictimport timefrom datetime import datetime, timedeltafrom faker import Fakerfrom utils import MyLoguru, GenerateToolsdef get_vars(content: str) -> List[str]:    """    è·å–å˜é‡    """    import re    pattern = r'(\w+)\s*='    matches = re.findall(pattern, content)    variables = [match.strip() for match in matches]    return variablesclass TargetOPT:    Before = "4"    After = "3"class ExecScriptForInterface:    faker = Faker(locale="zh_CN")    def __init__(self, funcStr: str, log: Any = None):        self.funcStr = funcStr        self.variables = {}        if log is None:            self.log = MyLoguru().get_logger()        else:            self.log = log        self.local_vars = {"log": self.log.info,                           "timestamp": self.__timeStamp,                           "faker": self.faker,                           "execute_sql": self.execute_sql,                           "getVar": self.get_var,                           "date": self.__calculate_date}    def exec_beforeFunc(self) -> Dict[str, Any]:        """        æ‰§è¡Œå‰ç½®æ–¹æ³•        """        self.log.info(f" ğŸš©ğŸš©ğŸš©ğŸš© å¼€å§‹æ‰§è¡Œå‰ç½®æ–¹æ³• ğŸš©ğŸš©ğŸš©ğŸš©")        _vars = get_vars(self.funcStr)        self._exec(_vars)        self.log.info(f"å‰ç½®æ–¹æ³•æ‰§è¡Œå®Œæˆ âœ…âœ…âœ…âœ…\n")        return self.variables    def get_var(self, key):        for k, v in self.variables.items():            if k == key:                return v        return None    def _exec(self, varList):        try:            exec(self.funcStr, self.local_vars)        except Exception as e:            self.log.info(f"æ‰§è¡Œæ–¹æ³•æŠ¥é”™ <{str(e)}>")        for variable_name, variable_value in self.local_vars.items():            if variable_name in varList:                obj = {variable_name: variable_value}                self.log.info(f"æ–¹æ³• å˜é‡ ğŸ‘‰ğŸ‘‰ğŸ‘‰ğŸ‘‰: {obj}")                self.variables.update(obj)                self.log.info(self.variables)    @staticmethod    def __timeStamp(t: str = None) -> int | None:        """        è¿”å›å¯¹åº”æ—¶é—´æˆ³        ï¼šparam t +1s ç§’  +1m åˆ† +1håˆ†é’Ÿ        """        if t is None:            return int(time.time() * 1000)        ts = int(t[1:-1])        unit = t[-1]        if t.startswith("+"):            match unit:                case "s":                    return int(time.time()) + int(ts[:-1])                case "m":                    return int((datetime.now() + timedelta(minutes=int(ts[:-1]))).timestamp())                case "h":                    return int((datetime.now() + timedelta(hours=int(ts[:-1]))).timestamp())        elif t.startswith("-"):            match unit:                case "s":                    return int(time.time()) - int(ts[:-1])                case "m":                    return int((datetime.now() - timedelta(minutes=int(ts[:-1]))).timestamp())                case "h":                    return int((datetime.now() - timedelta(hours=int(ts[:-1]))).timestamp())        else:            return None    @staticmethod    def __calculate_date(t: str = None) -> str | None:        current_date = datetime.today().strftime("%Y-%m-%d")        if not t:            return current_date        ts = int(t[1:-1])        if t.startswith("+"):            delta = {"d": timedelta(days=ts),                     "m": timedelta(weeks=ts * 4),                     "y": timedelta(days=ts * 365)}            return (datetime.today() + delta[t[-1]]).strftime("%Y-%m-%d")        elif t.startswith("-"):            delta = {"d": timedelta(days=ts),                     "m": timedelta(weeks=ts * 4),                     "y": timedelta(days=ts * 365)}            return (datetime.today() - delta[t[-1]]).strftime("%Y-%m-%d")        else:            return None    @staticmethod    def execute_sql(db: str, sql_content: str):        result = []        # result = MyOracle(city=db).fetchall_sql(sql_content)        return result# class MyExecFunc:#     """#     æ¥å£æ‰§è¡Œ python å‡½æ•°#     """#     response: Response#     faker = Faker(locale="zh-cn")##     def __init__(self, funcStr: str = None, sender: Sender = None, varList: List[Mapping[str, Any]] = None):#         """#         :param funcStr#         :param sender#         :param varList#         """#         self.funcStr = funcStr#         self.varList = varList#         self.infos = []#         self.assertInfo = []#         self.__sender_instance = sender#         self.local_vars = {"log": self.__log,#                            "timestamp": self._timeStamp,#                            "faker": self.faker,#                            "execute_sql": self.execute_sql,#                            "getVar": self.get_var,#                            "date": self.calculate_date}##     def _exec(self, varList):#         try:#             exec(self.funcStr, self.local_vars)#         except Exception as e:#             self.__log(f"æ‰§è¡Œæ–¹æ³•æŠ¥é”™ <{str(e)}>")#         for variable_name, variable_value in self.local_vars.items():##             if variable_name in varList:#                 log.debug(f"{variable_name} = {variable_value}")#                 obj = {variable_name: variable_value}#                 self.__log(f"æ–¹æ³• å˜é‡ ğŸ‘‰ğŸ‘‰ğŸ‘‰ğŸ‘‰: {obj}")#                 self.infos.append({"key": variable_name, "value": variable_value})##     def exec_afterFunc(self, response: Response):#         """#         æ‰§è¡ŒfuncStr#         :param response å“åº”#         :param vars å˜é‡#         """#         if response:#             self.response = response#         self.__log(f"ï¸ ğŸš©ğŸš©ğŸš©ğŸš© å¼€å§‹æ‰§è¡Œåç½®æ–¹æ³• ğŸš©ğŸš©ğŸš©ğŸš©")#         if self.funcStr:#             varList = get_