#!/usr/bin/env python# -*- coding:utf-8 -*-# @Time : 2024/7/23# @Author : cyq# @File : sender# @Software: PyCharm# @Desc:from typing import NoReturn, Union, Mapping, Dictfrom play.exception import APIAssertExceptionfrom play.writer import Writerfrom utils import logfrom utils.jsonPathUtil import MyJsonPathfrom app.model.ui import UIStepAPIModel, UIResultModel, UICaseStepsModelfrom play.extract import ExtractManagerfrom play.logWriter import LogWriterfrom utils.httpxClient import HttpxClientfrom utils.assertsUtil import MyAssertsfrom httpx import Response, Requestclass APISender(HttpxClient):    cookie: str = None    response: Response = None    def __init__(self, logger: LogWriter, em: ExtractManager):        self.em = em        self.log = logger        super().__init__(            hooks={'request': [self.__log_request],                   'response': [self.__log_response]},        )    async def __call__(self, env: str, stepApi: UIStepAPIModel, **kwargs) -> Union[Response, None]:        """        :return: response        """        try:            if self.cookie is None:                await self.log.write_log(f"ğŸš€ è·å–cookieå¤±è´¥ âŒ")                return None            kwargs["headers"] = {"Cookie": self.cookie}            if stepApi.params:                newParams = await self.__list2Dict(stepApi.params)                kwargs['params'] = await self.em.transform_target(newParams)            if stepApi.bodyType == 1:                kwargs["json"] = await self.em.transform_target(stepApi.body)            await self.log.write_log(f"ğŸš€ API Hook: request  headers={kwargs['headers']}")            await self.log.write_log(f"ğŸš€ API Hook: request  params={kwargs.get('params', None)}")            await self.log.write_log(f"ğŸš€ API Hook: request  body={kwargs.get('json', None)}")            self.response = await super().__call__(stepApi.method,                                                   env + stepApi.url,                                                   **kwargs)            await self.log.write_log(f"ğŸš€ API Hook: response text={self.response.text}")            return self.response        except Exception as e:            log.error(repr(e))            raise e    async def setCookie(self, cookie: Dict[str, str]) -> NoReturn:        """        :param cookie:        :return:        """        self.cookie = f"{cookie['name']}={cookie['value']}"    async def add_extracts(self, extracts: list):        """        å“åº” å˜é‡æ·»åŠ         '[{'id': 1723193047893, 'key': 'conId', 'value': '$.data.rows[0].conId', 'target': '1'}]        :param extracts:        :return:        """        for ext in extracts:            jp = MyJsonPath(jsonBody=self.response.json(),                            expr=ext['value'])            newValue = await jp.value()            await self.log.write_log(f"ğŸš€ API Var: æ·»åŠ å˜é‡ {ext['key']}={newValue}")            await self.em.add_var(key=ext['key'], value=newValue)    async def do_assert(self, stepApi: UIStepAPIModel,                        step: UICaseStepsModel,                        case_result: UIResultModel = None):        """        :param stepApi:        :param step        :param case_result        """        assert_manager = MyAsserts()        assertsInfo = []        for ass in stepApi.asserts:            await self.log.write_log(f"ğŸš€ API Assert: å¼€å§‹æ–­è¨€ {ass['desc']}")            jp = MyJsonPath(jsonBody=self.response.json(),                            expr=ass['extraValue'])            actualValue = await jp.value()            if actualValue is None:                await self.log.write_log(f"ğŸš€ API Assert:{ass['desc']} âš ï¸ JsonPathå–å€¼ä¸ºNone")            try:                assert_manager.option(assertOpt=ass['assertOpt'],                                      expect=ass["expect"],                                      actual=actualValue,                                      extraValueType=ass['extraValueType'])                await self.log.write_log(                    f"ğŸš€ API Assert:ã€{ass['desc']}ã€‘ æ–­è¨€æˆåŠŸ âœ… é¢„æœŸå€¼ï¼š{ass['expect']} å®é™…å€¼ï¼š{actualValue}")                # æ–­è¨€æˆåŠŸ æ·»åŠ ç»“æœ                assertsInfo.append(                    {**ass,                     "type": "API",                     "stepName": step.name,                     "actual": actualValue,                     "result": True}                )            except AssertionError:                # æ–­è¨€å¤±è´¥                assertsInfo.append(                    {**ass,                     "type": "API",                     "stepName": step.name,                     "actual": actualValue,                     "result": False}                )                await self.log.write_log(                    f"ğŸš€ API Assert:ã€{ass['desc']}ã€‘ é¢„æœŸå€¼ï¼š{ass['expect']} å®é™…å€¼ï¼š{actualValue}")                if stepApi.go_on == 0:                    await Writer.write_assert_info(case_result, assertsInfo)                    raise APIAssertException(f"ã€{ass['desc']}ã€‘ é¢„æœŸå€¼ï¼š{ass['expect']} å®é™…å€¼ï¼š{actualValue}")                else:                    continue        await Writer.write_assert_info(case_result, assertsInfo)    @staticmethod    async def __list2Dict(params: list) -> dict:        """        :param params:        :return:        """        return {i['key']: i['value'] for i in params}    async def __log_request(self, request: Request) -> None:        await self.log.write_log(f"ğŸš€ API Hook: request  {request.method.upper()} : {request.url}")    async def __log_response(self, response: Response) -> None:        await self.log.write_log(f"ğŸš€ API Hook: response status_code >> {response.status_code}")