#!/usr/bin/env python# -*- coding:utf-8 -*-# @Time : 2025/3/14# @Author : cyq# @File : tools# @Software: PyCharm# @Desc:import astfrom typing import Anyfrom urllib.parse import urlparse, parse_qs, ParseResultimport jmespathclass Tools:    @staticmethod    def parse_url(url: str) -> ParseResult:        """        {            "scheme": url_obj.scheme,            "netloc": url_obj.netloc,            "path": url_obj.path,            "params": url_obj.params,            "query": parse_qs(url_obj.query),            "fragment": url_obj.fragment        }        """        url_obj = urlparse(url)        return url_obj    @staticmethod    def str_trans(value: Any):        # 使用 literal_eval 转换字符串为对应的类型        try:            return ast.literal_eval(value)        except (ValueError, SyntaxError) as e:            # 如果无法转换，返回原始值            return valueif __name__ == '__main__':    a = {'code': 0, 'data': '张建华', 'msg': 'ok'}    e = "code"    print(jmespath.search(e, a))